#include <bits/stdc++.h>using namespace std; void solve() {    int n;    cin >> n;    vector<int > result;    int power=1;    while(n>0){        int digit=n%10;        if(digit!=0){            result.push_back(digit*power);         }        n/=10;        power*=10;    }    reverse(result.begin(),result.end());    cout<<result.size()<<endl;    for(int x:result){        cout<<x<<" ";    }    cout<<"\n";    } int main() {    ios::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin >> t;    while(t--) solve();    return 0;}